import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {addTodo, removeTodo, updateTodo, setEditable} from '../features/todo/todoSlice'

function Todos() {
    const todos = useSelector(state => state.todos)
    // const editable = useSelector(state => state.todos[todos.length-1].editable)
    // const [editable, setEditable] = useState(false)
    // const [msg, setMsg] = useState(todos.length > 0 ? todos[todos.length-1].text : '')
    // console.log("Todos component re-rendered")
    // console.log("msg: ", todos[todos.length-1].text)
    const dispatch = useDispatch()
    const update =(updatedTodo)=>{
      dispatch(updateTodo(updatedTodo));
    }

  return (
    <>
    <div>Todos</div>
    <ul className="list-none">
        {todos.map((todo) => (
          <li
            className="mt-4 flex justify-between items-center bg-zinc-800 px-4 py-2 rounded" 
            key={todo.id}
          >
          <input 
              type='text'
              value={todo.text}
              readOnly={!todo.editable}
              onChange={(e)=> {
                const updatedTodo = { id: todo.id, text: e.target.value };
                update(updatedTodo);


              }}

            />
            <div className='text-white'>{todo.text}</div>
            {console.log("todo.text: ", todo.text)}
            <button
             onClick={() => dispatch(removeTodo(todo.id))}
              className="text-white bg-red-500 border-0 py-1 px-4 focus:outline-none hover:bg-red-600 rounded text-md"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth={1.5}
                stroke="currentColor"
                className="w-6 h-6"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0"
                />
              </svg>
            </button>
            <button className='' onClick={()=> {
              dispatch(setEditable(todo.id))
            }}>
               {todo.editable ? 
                  "üìÅ"  : 
                  "‚úèÔ∏è" }
            </button> 
          </li>
        ))}
      </ul>
    </>
  )
}

export default Todos
