import {createSlice, nanoid } from '@reduxjs/toolkit';


const initialState = {
    // editable : false,
    todos: [
        {id: 1, text: "Hello world", editable : false}
    ] // {id: 1, text: "Hello world"}
}

export const todoSlice = createSlice({
    name: 'todo',
    initialState,
    reducers: {
        addTodo: (state, action) => {
            const todo = {
                id: nanoid(), 
                text: action.payload,
                editable: false
            }
            state.todos.push(todo)
        },
        removeTodo: (state, action) => {
            state.todos = state.todos.filter((todo) => todo.id !== action.payload )
        },
        updateTodo: (state, action) => {
            console.log("update called")
            state.todos = state.todos.map((todo) => todo.id === action.payload.id ? action.payload : todo)
            // console.log("updated todo..", state.todos)
        },
        // setEditable2: (state, action) => {
        //     console.log("set editable called", action.payload.id)
        //     state.todos = state.todos.map((todo) => todo.id === action.payload.id ? true : todo)
        //     console.log("set todo..", state.todos)
        // },

        setEditable : (state, action) => {
            state.todos = state.todos.map ((todo) => {
                if ( todo.id === action.payload.id){
                    console.log("Id matched", action.payload.id)
                    return{
                        ...todo,
                        editable: !todo.editable
                    }
                }
            })
        }
    }})

// they will help in individual components
export const {addTodo, removeTodo, updateTodo, setEditable} = todoSlice.actions

// to update store states
export default todoSlice.reducer
