import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {addTodo, removeTodo, updateTodo, toggleComplete} from '../features/todo/todoSlice'

function Todos({todo}) {

  const [isTodoEditable, setIsTodoEditable] = useState(false)
  const [todoMsg, setTodoMsg] = useState(todo.text)
  const dispatch = useDispatch()

  const editTodo = () => {
    dispatch(updateTodo( {id: todo.id, text: todoMsg}))
    console.log("Updation:", todo)
    setIsTodoEditable(false)
    // console.log(isTodoEditable)
  }
  const toggleCompleted = () => {
    console.log('dispatch triggered...', todo.completed );
    dispatch(toggleComplete({id: todo.id}))
    console.log('dispatch triggered after...', todo.completed)
  }

  return (
      <div
          className={`flex border border-black/10 rounded-lg px-3 py-1.5 gap-x-3 shadow-sm shadow-white/50 duration-300  text-black ${
              todo.completed ? "bg-[#c6e9a7]" : "bg-[#ccbed7]"
          }`}
      >
          <input
              type="checkbox"
              className="cursor-pointer"
              checked={todo.completed}
              onChange={toggleCompleted}
          />
          <input
              type="text"
              className={`border outline-none w-full  rounded-lg ${
                  isTodoEditable ? "border-black/10 px-2" : "border-transparent"
              } ${todo.completed ? "line-through" : ""}`}
              value={todoMsg}
              onChange={(e) => setTodoMsg(e.target.value)}
              readOnly={!isTodoEditable}
          />
          {/* Edit, Save Button */}
          <button
              className="inline-flex w-8 h-8 rounded-lg text-sm border border-black/10 justify-center items-center bg-gray-50 hover:bg-gray-100 shrink-0 disabled:opacity-50"
              onClick={() => {
                  if (todo.completed) return;

                  if (isTodoEditable) {
                      editTodo();
                      // console.log("AFTER VALUE: ", todo)
                  } else setIsTodoEditable((prev) => !prev);
              }}
              disabled={todo.completed}
          >
              {isTodoEditable ? "üìÅ" : "‚úèÔ∏è"}
          </button>
          {/* Delete Todo Button */}
          <button
              className="inline-flex w-8 h-8 rounded-lg text-sm border border-black/10 justify-center items-center bg-gray-50 hover:bg-gray-100 shrink-0"
              onClick={() => dispatch(removeTodo(todo.id))}
          >
              ‚ùå
          </button>

      </div>
  );
}

export default Todos
